#-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=> Arithmatic Op. <=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=--=-=-=-=-=-=-
x = 5
y = 2
print("Additon:- ", x+y) #add
print("Subtraction:- ", x-y) #sub
print("Multiplication:- ",x*y) #multiple
print("Power:- ",x**y) #power
print("Division with floating point:- ",x/y) #div
print("Division without floating point:- ",x//y) #div
print("Modulus:- ",x%y) #modulus
print(x+y*2)

